package com.loops;

/*26. Display 1, 2, 3, 4â€¦., 100 Numbers.*/

public class DisplayNumber01 {
	public static void main(String[] args) {
		displayNumber();
		
	}

	private static void displayNumber() {
		// TODO Auto-generated method stub
		
		for(int i=1;i<=100;i++) {
			System.out.print("\t\n" +i);
		}
	}
}
