package com.loops;

//30. Display 10, 9, 8, 7â€¦., 1 Numbers.//
public class DisplayNumbersTenToOne {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		TenToOne();
	}

	private static void TenToOne() {
		// TODO Auto-generated method stub
		
		for(int i=10;i>=1;i--) {
			System.out.print("\t" +i);
		}
	}

}
