package com.loops;

//31. Display -1, -2, -3, -4, â€¦, -10 Numbers.//

public class NegOneToNegTen {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		methodNeg();
	}

	private static void methodNeg() {
		
		for(int i=-1; i>=-10; i--) {
			System.out.println(i);
		}
		
	}

}
